$(document).on("pagebeforeshow",function(){$.mobile.activePage.find("[data-widget] > ul").trigger("prepare")});$(document).on("pageshow",function(){$.mobile.activePage.find("[data-widget] > ul").trigger("redraw")});$(document).delegate("[data-widget] > ul",{prepare:function(){$(this).children().hide()},redraw:function(){$(this).children().each(function(a){var b=$(this);setTimeout(function(){b.fadeIn()},100*(a+1))})}}); 
