var io={adress:'',port:'',read:function(item){io.get(item)},write:function(item,val){io.put(item,val)},init:function(address,port){io.address=address;io.port=port;io.stop()},run:function(realtime){widget.refresh();io.all();if(realtime)io.start()},timer:0,timer_run:false,loop:function(){if(widget.listening())io.timer=setTimeout('io.loop(); io.all();',1e3)},start:function(){if(!io.timer_run&&widget.listening()){io.loop();io.timer_run=true}},stop:function(){clearTimeout(io.timer);io.timer_run=false},get:function(item){$.ajax({url:"driver/io_json.php",data:{item:item},type:"GET",dataType:'json',async:true,cache:false}).done(function(response){widget.update(item,response[item])})},put:function(item,val){var timer_run=io.timer_run;io.stop();$.ajax({url:"driver/io_json.php",data:{item:item,val:val},type:"GET",dataType:'json',cache:false}).done(function(response){widget.update(item,response[item]);if(timer_run)io.start()})},all:function(){var items='';if(widget.listening()){var item=widget.listeners();for(var i=0;i<widget.listeners().length;i++)items+=item[i]+',';items=items.substr(0,items.length-1);$.ajax({url:'driver/io_json.php',data:{item:items},type:'POST',dataType:'json',async:true,cache:false}).done(function(response){$.each(response,function(item,val){widget.update(item,val)})})}}}