$(document).on("pagebeforeshow",function(a,b){$.mobile.activePage.find("[data-widget] > ul").trigger("prepare")});$(document).on("pageshow",function(a,b){$.mobile.activePage.find("[data-widget] > ul").trigger("redraw")});$(document).delegate("[data-widget] > ul",{prepare:function(a){$(this).children().hide()},redraw:function(a){$(this).children().each(function(a){var c=$(this);setTimeout(function(){c.fadeIn()},100*(a+1))})}}); 
